$('[data-fancybox="gallery"]').fancybox({
    toolbar: true,
    buttons: [
        'fullScreen',
        'thumbs',
        'close'
    ],
    /*图片加载后替换默认的下载链接*/
    afterShow: function(instance, current) {
        var downloadLink = $(current.opts.$orig).data('download');
        
        if (downloadLink) {
            // 查找 FancyBox 的下载按钮并修改其 href 属性
            instance.$refs.toolbar.find('.fancybox-button--download').attr('href', downloadLink);
        }
    }
});


//点击加载更多
jQuery(document).ready(function($) {
    // 初始化懒加载
    initLazyLoad();
    $('.next').click(function() {
        var $this = $(this);
        $this.addClass('loading').text('正在努力加载'); 
        var href = $this.attr('href'); 

        if (href != undefined) { 
            $.ajax({ 
                url: href,
                type: 'get',
                error: function(request) {
                    console.error('加载更多内容失败:', request);
                },
                success: function(data) { 
                    $this.removeClass('loading').text('点击查看更多'); 

                    // 提取新内容
                    var $res = $(data).find('.grid,.post,.book-item'); 
                    $('#photos,#bloglist,#books').append($res.fadeIn(500)); 

                    // 更新“加载更多”按钮的链接
                    var newhref = $(data).find('.next').attr('href'); 
                    if (newhref != undefined) {
                        $('.next').attr('href', newhref);
                    } else {
                        $('.next').remove(); 
                        document.getElementById("no_more").innerHTML = "—&nbsp;&nbsp;&nbsp;暂无更多内容&nbsp;&nbsp;&nbsp;—";
                    }

                    // 重新初始化懒加载逻辑
                    initLazyLoad();
                }
            });
        }
        return false;
    });
});

// 懒加载逻辑
function initLazyLoad() {
    const lazyImages = document.querySelectorAll('.lazy-load:not(.loaded)');
    const lazyLoad = (target) => {
        const io = new IntersectionObserver((entries, observer) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    const img = entry.target;
                    const src = img.getAttribute('data-src');

                    if (src) {
                        const tempImg = new Image();
                        tempImg.src = src;
                        tempImg.onload = () => {
                            img.style.backgroundImage = `url('${src}')`;
                            img.classList.add('loaded');
                            observer.unobserve(img);
                        };
                        tempImg.onerror = () => {
                            console.error('图片加载失败:', src);
                        };
                    }
                }
            });
        }, {
            rootMargin: '0px',
            threshold: 0.1
        });

        io.observe(target);
    };
    lazyImages.forEach(lazyLoad);
}


